    const flags = .{
        '-Wall',
        '-Wextra',
        '-Wpedantic',
        '-Werror=return-type',
        '-std=gnu23',
    };

    exe.addCSourceFile(.{
        .file = std.Build.LazyPath{ .cwd_relative = 'http.c' },
